// router.js - Sistema de rotas avan√ßado
class ZeniaRouter {
  constructor(routes) {
    this.routes = routes;
    this.currentRoute = null;
    this.history = [];
    this.init();
  }

  async init() {
    window.addEventListener('popstate', (e) => this.handleNavigation(e.state?.path));
    document.querySelectorAll('[data-route]').forEach(btn => {
      btn.addEventListener('click', (e) => {
        e.preventDefault();
        this.navigate(btn.dataset.route);
      });
    });
    await this.navigate(window.location.hash.slice(1) || 'home');
  }

  async navigate(routeName) {
    const route = this.routes[routeName];
    if (!route) return console.error(`Rota ${routeName} n√£o encontrada`);

    // Lazy loading de componentes
    const component = await route.component();
    const view = document.getElementById('router-view');

    // Transi√ß√£o suave
    view.style.opacity = '0';
    setTimeout(() => {
      view.innerHTML = component.render();
      component.mount?.();
      view.style.opacity = '1';
    }, 300);

    this.currentRoute = routeName;
    this.history.push(routeName);
    window.history.pushState({ path: routeName }, '', `#${routeName}`);
    this.updateNavActive(routeName);
  }

  updateNavActive(routeName) {
    document.querySelectorAll('.nav-btn').forEach(btn => {
      btn.classList.toggle('active', btn.dataset.route === routeName);
    });
  }
}

// Registro de rotas com lazy loading
const routes = {
  home: {
    component: () => import('./components/Home.js').then(m => new m.default())
  },
  chat: {
    component: () => import('./components/Chat.js').then(m => new m.default()),
    guards: ['auth']
  },
  perfil: {
    component: () => import('./components/Perfil.js').then(m => new m.default()),
    guards: ['auth']
  }
};

export default new ZeniaRouter(routes);

