// sw.js - Service Worker Z√©nia PWA
const CACHE_VERSION = 'zenia-v2.1.0';
const STATIC_CACHE = `static-${CACHE_VERSION}`;
const DYNAMIC_CACHE = `dynamic-${CACHE_VERSION}`;

const STATIC_ASSETS = [
  '/',
  '/index.html',
  '/css/main.css',
  '/js/app.js',
  '/img/zenia-logo.webp',
  '/manifest.json',
  '/offline.html'
];

// Estrat√©gia Cache First para est√°ticos
self.addEventListener('fetch', (event) => {
  const { request } = event;
  const url = new URL(request.url);

  if (STATIC_ASSETS.some(asset => url.pathname.endsWith(asset))) {
    event.respondWith(
      caches.match(request).then(cached => 
        cached || fetch(request).then(res => {
          const clone = res.clone();
          caches.open(STATIC_CACHE).then(cache => cache.put(request, clone));
          return res;
        }).catch(() => caches.match('/offline.html'))
      )
    );
    return;
  }

  // Network First para API com fallback
  if (url.hostname.includes('api-zenia')) {
    event.respondWith(
      fetch(request).then(res => {
        const clone = res.clone();
        caches.open(DYNAMIC_CACHE).then(cache => cache.put(request, clone));
        return res;
      }).catch(() => caches.match(request).then(cached => 
        cached || new Response(JSON.stringify({ offline: true }), {
          headers: { 'Content-Type': 'application/json' }
        })
      ))
    );
    return;
  }
});

// Background Sync para mensagens offline
self.addEventListener('sync', (event) => {
  if (event.tag === 'sync-messages') {
    event.waitUntil(
      self.registration.showNotification('Z√©nia', {
        body: 'Mensagens sincronizadas com sucesso! üöÄ'
      })
    );
  }
});

// Push Notifications
self.addEventListener('push', (event) => {
  const data = event.data?.json() || {};
  event.waitUntil(
    self.registration.showNotification(data.title || 'Z√©nia', {
      body: data.body,
      icon: '/img/zenia-icon-192.png',
      badge: '/img/zenia-badge-72.png',
      data: { url: data.url || '/chat' }
    })
  );
});

