// chat-client.js - Cliente WebSocket Z√©nia
class ZeniaChatClient {
  constructor(userId, token) {
    this.userId = userId;
    this.token = token;
    this.ws = null;
    this.messageQueue = [];
    this.reconnectAttempts = 0;
    this.maxReconnect = 5;
  }

  connect() {
    this.ws = new WebSocket(`wss://api-zenia.netlify.app/ws/chat?token=${this.token}`);

    this.ws.onopen = () => {
      console.log('‚úÖ Conectado ao Z√©nia Chat');
      this.reconnectAttempts = 0;
      this.flushMessageQueue();
    };

    this.ws.onmessage = (event) => this.handleMessage(JSON.parse(event.data));

    this.ws.onclose = () => this.handleReconnect();
    this.ws.onerror = (err) => console.error('‚ùå Erro WebSocket:', err);
  }

  sendMessage(roomId, content, options = {}) {
    const message = {
      type: 'message',
      payload: {
        roomId,
        content,
        timestamp: Date.now(),
        userId: this.userId,
        encrypted: options.encrypt ?? true,
        metadata: options.metadata || {}
      }
    };

    // Criptografia simples (em produ√ß√£o usar WebCrypto)
    if (message.payload.encrypted) {
      message.payload.content = btoa(unescape(encodeURIComponent(content)));
    }

    if (this.ws?.readyState === WebSocket.OPEN) {
      this.ws.send(JSON.stringify(message));
    } else {
      this.messageQueue.push(message);
    }
  }

  handleReconnect() {
    if (this.reconnectAttempts < this.maxReconnect) {
      const delay = Math.min(1000 * Math.pow(2, this.reconnectAttempts), 10000);
      setTimeout(() => {
        this.reconnectAttempts++;
        this.connect();
      }, delay);
    }
  }

  flushMessageQueue() {
    while (this.messageQueue.length && this.ws?.readyState === WebSocket.OPEN) {
      this.ws.send(JSON.stringify(this.messageQueue.shift()));
    }
  }
}

