class ZeniaArena {
  constructor(canvas) {
    this.scene = new THREE.Scene();
    this.camera = new THREE.PerspectiveCamera(75, canvas.width/canvas.height, 0.1, 1000);
    this.renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
    this.objects = [];
    this.init();
  }

  init() {
    // Ilumina√ß√£o
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
    this.scene.add(ambientLight);

    const dirLight = new THREE.DirectionalLight(0x7b3ff2, 0.8);
    dirLight.position.set(10, 20, 15);
    this.scene.add(dirLight);

    // Ch√£o com grid
    const gridHelper = new THREE.GridHelper(50, 50, 0x7b3ff2, 0x333355);
    this.scene.add(gridHelper);

    // Jogador
    this.player = new THREE.Mesh(
      new THREE.BoxGeometry(1, 2, 1),
      new THREE.MeshStandardMaterial({ color: 0x7b3ff2, metalness: 0.3 })
    );
    this.player.position.y = 1;
    this.scene.add(this.player);

    // C√¢mera segue jogador
    this.camera.position.set(0, 5, 10);
    this.camera.lookAt(this.player.position);

    // Loop de anima√ß√£o
    this.animate();
  }

  animate = () => {
    requestAnimationFrame(this.animate);
    this.updatePhysics();
    this.renderer.render(this.scene, this.camera);
  };

  updatePhysics() {
    // F√≠sica simplificada
    this.objects.forEach(obj => {
      if (obj.velocity) {
        obj.position.add(obj.velocity);
        // Colis√£o com ch√£o
        if (obj.position.y < obj.geometry.parameters.height/2) {
          obj.position.y = obj.geometry.parameters.height/2;
          obj.velocity.y *= -0.5; // Bounce
        }
      }
    });
  }
}

export default ZeniaArena;

