import { createContext, useContext, useReducer, useEffect } from 'react';
import { ZeniaAPI } from './services/api';

// Contexto global
const ZeniaContext = createContext();

// Reducer para estado global
const zeniaReducer = (state, action) => {
  switch(action.type) {
    case 'SET_USER': return { ...state, user: action.payload };
    case 'ADD_MESSAGE': return { 
      ...state, 
      messages: [...state.messages, action.payload],
      lastUpdate: Date.now()
    };
    case 'SET_LOADING': return { ...state, loading: action.payload };
    default: return state;
  }
};

// Provider
export const ZeniaProvider = ({ children }) => {
  const [state, dispatch] = useReducer(zeniaReducer, {
    user: null,
    messages: [],
    loading: false,
    theme: 'dark'
  });

  // Auto-save de mensagens
  useEffect(() => {
    if (state.messages.length > 0) {
      const timer = setTimeout(() => {
        localStorage.setItem('zenia_messages', JSON.stringify(state.messages.slice(-50)));
      }, 1000);
      return () => clearTimeout(timer);
    }
  }, [state.messages]);

  return (
    <ZeniaContext.Provider value={{ state, dispatch }}>
      {children}
    </ZeniaContext.Provider>
  );
};

// Hook personalizado
export const useZenia = () => {
  const context = useContext(ZeniaContext);
  if (!context) throw new Error('useZenia deve ser usado dentro de ZeniaProvider');
  return context;
};

