import { createApp, ref, computed, onMounted } from 'vue';
import { createPinia } from 'pinia';
import ZeniaAPI from './composables/useZeniaAPI';

// Composable para l√≥gica reutiliz√°vel
export function useChat() {
  const messages = ref([]);
  const isLoading = ref(false);
  const { sendMessage, fetchHistory } = ZeniaAPI();

  const filteredMessages = computed(() => 
    messages.value.filter(m => m.visible !== false)
  );

  const addMessage = async (content, role = 'user') => {
    isLoading.value = true;
    try {
      const response = await sendMessage(content, role);
      messages.value.push({
        id: Date.now(),
        content,
        role,
        timestamp: new Date(),
        ...response
      });
    } finally {
      isLoading.value = false;
    }
  };

  onMounted(async () => {
    messages.value = await fetchHistory();
  });

  return { messages, filteredMessages, isLoading, addMessage };
}

