import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { BehaviorSubject, Observable, retry, catchError } from 'rxjs';
import { Message, ChatResponse } from '../models/chat.models';

@Injectable({ providedIn: 'root' })
export class ZeniaChatService {
  private apiUrl = 'https://api-zenia.netlify.app/api/chat';
  private messagesSubject = new BehaviorSubject<Message[]>([]);
  public messages$ = this.messagesSubject.asObservable();

  constructor(private http: HttpClient) {}

  sendMessage(content: string, context?: any): Observable<ChatResponse> {
    const payload = {
      message: content,
      timestamp: new Date().toISOString(),
      context: context || {},
      metadata: { client: 'angular', version: '2.1' }
    };

    return this.http.post<ChatResponse>(this.apiUrl, payload).pipe(
      retry(2),
      catchError(error => {
        console.error('Erro ao enviar mensagem:', error);
        throw error;
      })
    );
  }

  addMessageToLocal(message: Message) {
    const current = this.messagesSubject.value;
    this.messagesSubject.next([...current, message]);
  }

  clearHistory() {
    this.messagesSubject.next([]);
    localStorage.removeItem('zenia_chat_history');
  }
}

