class ChatAPIView(APIView):
    permission_classes = [IsAuthenticatedOrReadOnly]
    throttle_classes = [ChatRateThrottle]

    def post(self, request, format=None):
        """Processar mensagem e gerar resposta da IA Z√©nia"""
