#include <fcgiapp.h>
#include <string>
#include <json/json.h>
#include "zenia_ai.hpp"

class ZeniaFCGIServer {
private:
    ZeniaAI ai_engine;

    std::string handlePostRequest(const std::string& body) {
        Json::Value root;
        Json::Reader reader;

        if (!reader.parse(body, root)) {
            return createErrorResponse(400, "JSON inv√°lido");
        }

        if (!root.isMember("message") || root["message"].asString().empty()) {
            return createErrorResponse(400, "Mensagem √© obrigat√≥ria");
        }

        try {
            auto response = ai_engine.generate(
                root["message"].asString(),
                root.get("context", Json::Value()),
                root.get("history", Json::Value())
            );

            Json::Value result;
            result["success"] = true;
            result["reply"] = response.text;
            result["metadata"]["model"] = "zenia-v2.1";
            result["metadata"]["timestamp"] = getCurrentTimestamp();
            result["metadata"]["tokens"] = response.usage;

            return createSuccessResponse(result);

        } catch (const std::exception& e) {
            return createErrorResponse(500, std::string("Erro: ") + e.what());
        }
    }

    std::string createErrorResponse(int code, const std::string& message) {
        Json::Value error;
        error["error"] = message;
        return formatFCGIResponse(code, "application/json", error.toStyledString());
    }

    std::string createSuccessResponse(const Json::Value& data) {
        return formatFCGIResponse(200, "application/json", data.toStyledString());
    }

    std::string formatFCGIResponse(int status, const std::string& contentType, const std::string& body) {
        return "Status: " + std::to_string(status) + " OK\r\n" +
               "Content-Type: " + contentType + "\r\n" +
               "Access-Control-Allow-Origin: *\r\n" +
               "\r\n" + body;
    }

public:
    void run() {
        while (FCGI_Accept() >= 0) {
            std::string method = getenv("REQUEST_METHOD") ? getenv("REQUEST_METHOD") : "";
            std::string body = getRequestBody();

            std::string response;

            if (method == "POST") {
                response = handlePostRequest(body);
            } else if (method == "GET") {
                Json::Value health;
                health["status"] = "ok";
                health["version"] = "2.1.0";
                response = createSuccessResponse(health);
            } else {
                response = createErrorResponse(405, "M√©todo n√£o permitido");
            }

            printf("%s", response.c_str());
        }
    }
};

int main() {
    ZeniaFCGIServer server;
    server.run();
    return 0;
