	aEngine *ai.ZeniaEngine
)

func main() {
	// Setup
	rdb = redis.NewClient(&redis.Options{Addr: "localhost:6379"})
	aEngine = ai.NewZeniaEngine("zenia-v2.1")

	r := gin.Default()

	// CORS middleware
	r.Use(func(c *gin.Context) {
		c.Writer.Header().Set("Access-Control-Allow-Origin", "https://zenia-3.netlify.app")
		c.Writer.Header().Set("Access-Control-Allow-Methods", "POST, GET, OPTIONS")
		c.Writer.Header().Set("Access-Control-Allow-Headers", "Content-Type")
		if c.Request.Method == "OPTIONS" {
			c.AbortWithStatus(204)
			return
		}
		c.Next()
	})

	// Rate limiting middleware
	r.Use(rateLimitMiddleware)

	// Routes
	r.POST("/api/chat", handleChat)
	r.GET("/api/health", healthCheck)

	log.Println("üöÄ Servidor Z√©nia Go rodando na porta 8080")
	r.Run(":8080")
}

func handleChat(c *gin.Context) {
	var req ChatRequest
	if err := c.ShouldBindJSON(&req); err != nil {
		c.JSON(http.StatusBadRequest, gin.H{"error": err.Error()})
		return
	}

	start := time.Now()

	resp, err := aEngine.Generate(req.Message, req.Context, req.History)
	if err != nil {
		log.Printf("Erro ao gerar resposta: %v", err)
		c.JSON(http.StatusInternalServerError, gin.H{"error": "Erro interno"})
		return
	}

	response := ChatResponse{
		Success: true,
		Reply:   resp.Text,
		Metadata: ResponseMetadata{
			Model:        "zenia-v2.1",
			Timestamp:    time.Now().UTC(),
			TokensUsed:   resp.Usage,
			ProcessingMs: time.Since(start).Milliseconds(),
		},
	}

	c.JSON(http.StatusOK, response)
}

func healthCheck(c *gin.Context) {
	c.JSON(http.StatusOK, gin.H{
		"status":  "healthy",
		"version": "2.1.0",
		"time":    time.Now().UTC(),
	})
}

func rateLimitMiddleware(c *gin.Context) {
	ip := c.ClientIP()
	key := "rate:" + ip

	count, _ := rdb.Get(c.Request.Context(), key).Int()
	if count > 100 {
		c.JSON(http.StatusTooManyRequests, gin.H{"error": "Muitas requisi√ß√µes"})
		c.Abort()
		return
	}

	rdb.Incr(c.Request.Context(), key)
	rdb.Expire(c.Request.Context(), key, 15*time.Minute)
	c.Next()
}

